import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import './Cart.css';

const Cart: React.FC = () => {
  const { cart, setCart } = useContext(CartContext)!;
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('John Doe');
  const [address, setAddress] = useState('123 Street, City');
  const navigate = useNavigate();

  const handleCheckout = () => {
    setShowForm(true);
  };

  const confirmOrder = () => {
    alert('Order placed successfully!');
    setCart([]);
    navigate('/');
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty. Add some products!</p>
      ) : (
        <>
          <div className="cart-items">
            {cart.map((item) => (
              <div className="cart-item" key={item.id}>
                <img src={item.thumbnail} alt={item.title} />
                <div className="cart-details">
                  <h3>{item.title}</h3>
                  <p>₹{item.price} × {item.quantity}</p>
                  <p>Total: ₹{item.price * item.quantity}</p>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-summary">
            <h2>Total: ₹{total}</h2>
            {!showForm && (
              <button onClick={handleCheckout} className="checkout-btn">Proceed to Checkout</button>
            )}
            {showForm && (
              <div className="checkout-form">
                <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Your Name" />
                <textarea value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Your Address" />
                <button onClick={confirmOrder} className="confirm-btn">Confirm Order</button>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
