import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext'; // Use CartContext hook
import { useUser } from '../context/UserContext'; // Use UserContext hook

const NavBar = () => {
  const { cart } = useCart(); // Get cart from CartContext
  const { user, setUser } = useUser(); // Get user from UserContext
  const [cartTotal, setCartTotal] = useState(0);

  useEffect(() => {
    // Update the total cart value
    const total = cart.reduce((acc, item) => acc + item.price, 0);
    setCartTotal(total);
  }, [cart]);

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('username');
    setUser(null); // Update user context
  };

  return (
    <nav style={{ padding: '10px', backgroundColor: '#333', color: '#fff' }}>
      <ul style={{ display: 'flex', justifyContent: 'space-between' }}>
        <li><Link to="/" style={{ color: '#fff', textDecoration: 'none' }}>Home</Link></li>
        <li><Link to="/cart" style={{ color: '#fff', textDecoration: 'none' }}>Cart</Link></li>
        {user ? (
          <>
            <li style={{ color: '#fff' }}>Hello, {user}</li>
            <li style={{ color: '#fff' }}>Total: ${cartTotal}</li>
            <li><button onClick={handleLogout} style={{ padding: '5px 10px' }}>Logout</button></li>
          </>
        ) : (
          <li><Link to="/login" style={{ color: '#fff', textDecoration: 'none' }}>Login</Link></li>
        )}
      </ul>
    </nav>
  );
};

export default NavBar;
