import React, { useEffect, useState } from 'react';
import { Box, Grid, Typography, MenuItem, Select, CircularProgress, Card, CardMedia, CardContent, CardActions, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';
import useStore from '../store/useStore';

const Home = () => {
  const [products, setProducts] = useState<any[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [sortOption, setSortOption] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [skip, setSkip] = useState<number>(0);
  const limit = 12;
  const addToCart = useStore((state) => state.addToCart);
  const navigate = useNavigate();

  const fetchProducts = async () => {
    setLoading(true);
    const url = selectedCategory
      ? `/products/category/${selectedCategory}`
      : `/products?limit=${limit}&skip=${skip}`;
    const res = await API.get(url);
    let data = res.data.products;

    if (sortOption === 'low') data.sort((a: any, b: any) => a.price - b.price);
    else if (sortOption === 'high') data.sort((a: any, b: any) => b.price - a.price);

    setProducts(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchProducts();
  }, [selectedCategory, sortOption, skip]);

  useEffect(() => {
    API.get('/products/categories').then((res) => setCategories(res.data));
  }, []);

  return (
    <Box p={2}>
      <Typography variant="h4" gutterBottom>Products</Typography>

      <Box display="flex" gap={2} mb={2}>
        <Select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)} displayEmpty>
          <MenuItem value="">All Categories</MenuItem>
          {categories.map((cat) => <MenuItem key={cat} value={cat}>{cat}</MenuItem>)}
        </Select>
        <Select value={sortOption} onChange={(e) => setSortOption(e.target.value)} displayEmpty>
          <MenuItem value="">Sort by</MenuItem>
          <MenuItem value="low">Price: Low to High</MenuItem>
          <MenuItem value="high">Price: High to Low</MenuItem>
        </Select>
      </Box>

      {loading ? <CircularProgress /> : (
        <Grid container spacing={2}>
          {products.map((prod: any) => (
            <Grid item xs={12} sm={6} md={3} key={prod.id}>
              <Card>
                <CardMedia
                  component="img"
                  height="160"
                  image={prod.thumbnail}
                  onClick={() => navigate(`/product/${prod.id}`)}
                  sx={{ cursor: 'pointer' }}
                />
                <CardContent>
                  <Typography variant="subtitle1">{prod.title}</Typography>
                  <Typography variant="body2">â‚¹{prod.price}</Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" onClick={() => addToCart(prod)}>Add to Cart</Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      <Box mt={3} display="flex" gap={1}>
        <Button disabled={skip === 0} onClick={() => setSkip(skip - limit)}>Previous</Button>
        <Button onClick={() => setSkip(skip + limit)}>Next</Button>
      </Box>
    </Box>
  );
};

export default Home;
