// src/pages/Cart.tsx

import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext'; // Import useCart from context

interface CartItem {
  id: number;
  price: number;
  quantity: number;
  thumbnail?: string;
  title: string;
}

const Cart: React.FC = () => {
  const { cart } = useCart(); // Get cart data from context
  const [cartTotal, setCartTotal] = useState<number>(0);

  useEffect(() => {
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setCartTotal(total);
  }, [cart]);

  const handleCheckout = () => {
    alert('Proceeding to checkout');
  };

  const defaultThumbnail = 'https://via.placeholder.com/100'; // Default image URL for items without a thumbnail

  return (
    <div>
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.map((item) => (
          <div className="cart-item" key={item.id}>
            <img src={item.thumbnail || defaultThumbnail} alt={item.title} />
            <div className="cart-details">
              <h3>{item.title}</h3>
              <p>₹{item.price} × {item.quantity}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-total">
        <h3>Total: ₹{cartTotal}</h3>
        <button onClick={handleCheckout}>Proceed to Checkout</button>
      </div>
    </div>
  );
};

export default Cart;
