// src/pages/Home.tsx

import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext'; // Import useCart from context

interface Product {
  id: number;
  title: string;
  price: number;
  thumbnail: string;
}

const Home: React.FC = () => {
  const { cart, setCart } = useCart(); // Get and set cart from context
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    // Simulating fetching products (replace with actual API call)
    const fetchedProducts = [
      { id: 1, title: 'Product 1', price: 100, thumbnail: 'https://via.placeholder.com/100' },
      { id: 2, title: 'Product 2', price: 200, thumbnail: 'https://via.placeholder.com/100' },
    ];
    setProducts(fetchedProducts);
  }, []);

  const addToCart = (product: Product) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === product.id);
      if (existingItem) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  return (
    <div>
      <h2>Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <img src={product.thumbnail} alt={product.title} />
            <h3>{product.title}</h3>
            <p>â‚¹{product.price}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
