import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { UserContext } from '../context/UserContext';

const Navbar = () => {
  const cartContext = useContext(CartContext);
  const userContext = useContext(UserContext);

  const cart = cartContext?.cart ?? [];
  const setCart = cartContext?.setCart ?? (() => {});
  const addToCart = cartContext?.addToCart ?? (() => {});

  const user = userContext?.user ?? null;
  const setUser = userContext?.setUser ?? (() => {});

  const [cartTotal, setCartTotal] = useState<number>(0);
  const navigate = useNavigate();

  useEffect(() => {
    const total = cart.reduce((acc: number, item: any) => acc + item.price * item.quantity, 0);
    setCartTotal(total);
  }, [cart]);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <nav style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>
      <Link to="/">Products</Link> | <Link to="/cart">Cart ({cart.length})</Link> | â‚¹{cartTotal.toFixed(2)}
      {user ? (
        <>
          <span style={{ marginLeft: '20px' }}>Welcome, {user.firstName}</span>
          <button onClick={handleLogout} style={{ marginLeft: '10px' }}>Logout</button>
        </>
      ) : (
        <Link to="/login" style={{ marginLeft: '20px' }}>Login</Link>
      )}
    </nav>
  );
};

export default Navbar;
