// src/components/Navbar.tsx

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext'; // Import useCart from context

const Navbar: React.FC = () => {
  const { cart } = useCart(); // Get cart data from context
  const [cartTotal, setCartTotal] = useState<number>(0);

  useEffect(() => {
    const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setCartTotal(total);
  }, [cart]);

  const user = { name: 'John Doe' }; // Hardcoded user (can come from user context)

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/cart">Cart ({cart.length}) - â‚¹{cartTotal}</Link></li>
        <li>{user ? `Welcome, ${user.name}` : 'Login'}</li>
      </ul>
    </nav>
  );
};

export default Navbar;
