import { Box, Typography, Button, Card, CardContent, IconButton, TextField } from '@mui/material';
import useStore from '../store/useStore';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const { cart, updateQuantity, removeFromCart } = useStore();
  const navigate = useNavigate();
  const total = cart.reduce((acc, item) => acc + item.price * (item.quantity || 1), 0);

  return (
    <Box p={2}>
      <Typography variant="h4">Your Cart</Typography>
      {cart.length === 0 ? <Typography>Your cart is empty.</Typography> : (
        <>
          {cart.map((item) => (
            <Card key={item.id} sx={{ my: 2 }}>
              <CardContent sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <img src={item.thumbnail} alt={item.title} width={80} />
                <Typography>{item.title}</Typography>
                <Typography>₹{item.price}</Typography>
                <TextField
                  type="number"
                  value={item.quantity}
                  onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))}
                  sx={{ width: 60 }}
                  inputProps={{ min: 1 }}
                />
                <IconButton onClick={() => removeFromCart(item.id)}><DeleteIcon /></IconButton>
              </CardContent>
            </Card>
          ))}
          <Typography variant="h6">Total: ₹{total.toFixed(2)}</Typography>
          <Button variant="contained" color="primary" onClick={() => navigate('/checkout')}>Checkout</Button>
        </>
      )}
    </Box>
  );
};

export default Cart;
