import React, { useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';  // Ensure this import is correct

interface CartItem {
  id: number;
  title: string;
  price: number;
  quantity: number;
  thumbnail: string;
}

const Cart: React.FC = () => {
  const { cart, setCart } = useContext(CartContext)!;
  const navigate = useNavigate();
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState('John Doe');

  const defaultThumbnail = 'path/to/default-image.jpg'; // Replace with actual default image path

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = () => {
    setShowForm(true);
  };

  const handleOrderPlaced = () => {
    alert('Order placed!');
    setCart([]); // Clear cart
    navigate('/'); // Navigate back to home page
  };

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.map((item) => (
          <div className="cart-item" key={item.id}>
            <img src={item.thumbnail || defaultThumbnail} alt={item.title} />
            <div className="cart-details">
              <h3>{item.title}</h3>
              <p>₹{item.price} × {item.quantity}</p>
              <p>Total: ₹{item.price * item.quantity}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-summary">
        <h3>Total: ₹{total}</h3>
        <button onClick={handleCheckout}>Proceed to Checkout</button>
      </div>
      {showForm && (
        <div className="checkout-form">
          <h3>Checkout</h3>
          <form onSubmit={handleOrderPlaced}>
            <label>Name: </label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <button type="submit">Place Order</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Cart;
