import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { useUser } from '../context/UserContext';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

export default function NavBar() {
  const { user, isLoggedIn } = useUser();
  const { total } = useCart();

  return (
    <AppBar position="static">
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>E-Commerce</Link>
        </Typography>

        <Box sx={{ marginRight: 2 }}>
          {isLoggedIn ? (
            <Typography variant="body1">Welcome, {user?.firstName}</Typography>
          ) : (
            <Typography variant="body1">Logged Out</Typography>
          )}
        </Box>

        <Box sx={{ marginRight: 2 }}>
          <Typography variant="body1">Cart: ${total.toFixed(2)}</Typography>
        </Box>

        <Button color="inherit" component={Link} to="/products">Products</Button>
        <Button color="inherit" component={Link} to="/cart">Cart</Button>
      </Toolbar>
    </AppBar>
  );
}
