import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { UserContext } from '../context/UserContext';

const Navbar = () => {
  const cartContext = useContext(CartContext);
  const userContext = useContext(UserContext);
  const navigate = useNavigate();

  // Prevent crashes if context isn't available
  if (!cartContext || !userContext) return null;

  const { cart } = cartContext;
  const { user, setUser } = userContext;
  const [cartTotal, setCartTotal] = useState<number>(0);

  useEffect(() => {
    const total = cart.reduce((acc: number, item: { price: number }) => acc + item.price, 0);
    setCartTotal(total);
  }, [cart]);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '1rem', backgroundColor: '#f0f0f0' }}>
      <div>
        <Link to="/" style={{ marginRight: '1rem' }}>Products</Link>
        <Link to="/cart">Cart</Link>
      </div>
      <div>
        {user ? (
          <>
            <span style={{ marginRight: '1rem' }}>Welcome, {user.firstName}</span>
            <span style={{ marginRight: '1rem' }}>Total: â‚¹{cartTotal.toFixed(2)}</span>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
