import { Box, Typography, TextField, Button, Snackbar } from '@mui/material';
import { useState } from 'react';
import useStore from '../store/useStore';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [open, setOpen] = useState(false);
  const login = useStore((state) => state.login);
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!username) return;
    const dummyToken = 'dummytoken123';
    login(dummyToken, { firstName: username });
    setOpen(true);
    setTimeout(() => navigate('/'), 1000);
  };

  return (
    <Box p={2}>
      <Typography variant="h4">Login</Typography>
      <TextField label="Username" fullWidth sx={{ my: 2 }} value={username} onChange={(e) => setUsername(e.target.value)} />
      <Button variant="contained" onClick={handleLogin}>Login</Button>

      <Snackbar open={open} autoHideDuration={3000} message="Login successful!" />
    </Box>
  );
};

export default Login;
